import { defineStore } from 'pinia' 

export const useGuestStore = defineStore('guest', {
    state: () => {
        return {
            guest: {
                name: '',
                id: null,
                email: '',
            },
            isAuthenticated: false,
            token: '',
            isLoading: false
        }
    },
    getters: {
        get_Guest: (state) => state.guest,
        isAuth: (state) => state.isAuth,
        isloading: (state) => state.isLoading
    },
    actions: {
        loading(status){
            this.isLoading = status
        },
    }
})
