export default {
  name: 'Projects',
  data() {
    return {
      projects: []
    };
  },
  components: {
    ProjectBox
  },
  mounted() {
    this.get_data();
  },
  methods: {
    ...mapActions(useGuestStore, ["loading"]),
    async get_data() {
      this.loading(true);
      await axios.get('/api/v1/projects').then(response => {
        console.log(response.data);
        this.projects = response.data;
      }).catch(error => {
        console.log(JSON.stringify(error));
      });
      this.loading(false);
    }
  }
};
